pragma solidity >= 0.6.12;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/ERC20Burnable.sol";


contract RouterERC20 is ERC20, Ownable, ERC20Burnable{

    uint8 private _decimals;

    constructor(string memory name, string memory symbol, uint8 decimals) public ERC20(name, symbol) {
        _setupDecimals(decimals);
    }


    /**
        @notice mints ROUTE-LP token
        @param _to Recipient Address.
        @param _amount Amount that needs to be isLpToken.
     */
    function mint(address _to, uint256 _amount) public onlyOwner returns(bool) {
        _mint(_to, _amount);
        return true;
    }
    /**
        @notice burns ROUTE-LP token
        @param _from Senders Address.
        @param _amount Amount that needs to be burned.
     */
    function burn(address _from, uint256 _amount) public onlyOwner returns(bool) {
        _burn(_from, _amount);
        return true;
    }
}

